-- ------------------------------------------------------- CREATE USER & TABLESPACE-- -----------------------------------------------------CREATE SMALLFILE TABLESPACE ${storage.jdbc.username}  DATAFILE '${storage.jdbc.username}_01.dat' SIZE 100 M REUSE AUTOEXTEND ON NEXT 50 M;CREATE USER ${storage.jdbc.username} IDENTIFIED BY ${storage.jdbc.password}  DEFAULT TABLESPACE ${storage.jdbc.username} TEMPORARY TABLESPACE TEMP ACCOUNT UNLOCK;-- ------------------------------------------------------- USER PRIVILEGES-- -----------------------------------------------------GRANT CREATE SEQUENCE TO ${storage.jdbc.username};GRANT CREATE TABLE TO ${storage.jdbc.username};GRANT CREATE VIEW TO ${storage.jdbc.username};GRANT CREATE MATERIALIZED VIEW TO ${storage.jdbc.username};GRANT CREATE SESSION TO ${storage.jdbc.username};GRANT CREATE PROCEDURE TO ${storage.jdbc.username};GRANT CREATE SYNONYM TO ${storage.jdbc.username};GRANT CREATE TRIGGER TO ${storage.jdbc.username};GRANT EXECUTE ON DBMS_LOCK TO ${storage.jdbc.username};ALTER USER ${storage.jdbc.username} QUOTA UNLIMITED ON ${storage.jdbc.username};